import { Component } from '@angular/core';

@Component({
  selector: 'myPipes-app',
  templateUrl: './myPipes.component.html',
  // styleUrls: ['./app.component.css']
})
export class MyPipesComponent {
  servers = [
    {
      instanceType: 'medium',
      name: 'Production Server',
      status: 'stable',
      started: new Date(15, 1, 2017),
    },
    {
      instanceType: 'large',
      name: 'User Database',
      status: 'stable',
      started: new Date(15, 1, 2017),
    },
    {
      instanceType: 'small',
      name: 'Development Server',
      status: 'offline',
      started: new Date(15, 1, 2017),
    },
    {
      instanceType: 'small',
      name: 'Testing Environment Server',
      status: 'stable',
      started: new Date(15, 1, 2017),
    },
  ];
  fillteredStatus = '';
  getStatusClasses(server: {
    instanceType: string;
    name: string;
    status: string;
    started: Date;
  }) {
    return {
      'list-group-item-success': server.status === 'stable',
      'list-group-item-warning': server.status === 'offline',
      'list-group-item-danger': server.status === 'critical',
    };
  }
}
<div class="container">
  <div class="row">
    <div class="col-xs-12 col-sm-10 col-md-8 col-sm-offset-1 col-md-offset-2">
      <input type="text" [(ngModel)]="fillteredStatus" />
      <hr />
      <ul class="list-group">
        <li
          class="list-group-item"
          *ngFor="let server of servers |filter:fillteredStatus:'status'"

          [ngClass]="getStatusClasses(server)"
        >
          <span class="badge" style="color:green">
            {{ server.status }}
          </span>
          <strong>{{ server.name | shorten: 10 }}</strong> |
          {{ server.instanceType | uppercase }} |
          {{ server.started | date: 'short' | uppercase }}
        </li>
      </ul>
    </div>
  </div>
</div>
